<?php	 	 class RupiahMaker{	 		 public static function convertInt($data)	{				// $rupiah = "";				// $jml = strlen($data);			// while($jml > 3){					// $rupiah = "." . substr($data,-3) . $rupiah;					// $l = strlen($data) - 3;					// $data = substr($data,0,$l);					// $jml = strlen($data);			// }			 // $rupiah = "Rp" . $data . $rupiah . ",00";					 // return $rupiah;				 return 'Rp'.strrev(implode('.',str_split(strrev(strval($data)),3))).',00';		 }		 		 public static function terbilangMaker($satuan){			$huruf = array ("", "satu", "dua", "tiga", "empat", "lima", "enam", "tujuh", "delapan", "sembilan", "sepuluh","sebelas");  			if ($satuan < 12)  				return " ".$huruf[$satuan];  			elseif ($satuan < 20)  				return RupiahMaker::terbilangMaker($satuan - 10)." belas";  			elseif ($satuan < 100)  				return RupiahMaker::terbilangMaker($satuan / 10)." puluh". RupiahMaker::terbilangMaker($satuan % 10);  			elseif ($satuan < 200)  				return "seratus".RupiahMaker::terbilangMaker($satuan - 100);  			elseif ($satuan < 1000)  				return RupiahMaker::terbilangMaker($satuan / 100)." ratus". RupiahMaker::terbilangMaker($satuan % 100);  			elseif ($satuan < 2000)  				return "seribu".RupiahMaker::terbilangMaker($satuan - 1000);   			elseif ($satuan < 1000000)  				return RupiahMaker::terbilangMaker($satuan / 1000)." ribu". RupiahMaker::terbilangMaker($satuan % 1000);   			elseif ($satuan < 1000000000)  				return RupiahMaker::terbilangMaker($satuan / 1000000)." juta". RupiahMaker::terbilangMaker($satuan % 1000000);   			elseif ($satuan < 1000000000000)  				return RupiahMaker::terbilangMaker($satuan / 1000000000) ." miliar" . RupiahMaker::terbilangMaker($satuan % 1000000000);   			elseif ($satuan < 1000000000000000)  //entah kenapa error yg triliun				return RupiahMaker::terbilangMaker($satuan / 1000000000000) ." triliun" . RupiahMaker::terbilangMaker($satuan % 1000000000000);   			elseif ($satuan >= 1000000000000000)  				echo "Angka terlalu Besar";  			 		 }		 	}?>